catalog_script_client.script:
function onLoad() {

    var branchName = g_form.getValue('branch_name');
    var branchNameNoSpaces = branchName.replace(" ", "_");
    var branchNameNoSemiC = branchNameNoSpaces.replace(":", "-");

    g_form.setReadOnly('branch_name', true);

    var newBranchName = branchNameNoSemiC.substring(0, branchNameNoSpaces.length - 3);
    //g_form.setValue('branch_name', newBranchName);

    g_form.setValue('create_webex_teams_room', 'true');
    g_form.setVisible('webex_teams_room_name', 'false');
    g_form.setVisible('webex_user_email_address', 'false');
    g_form.setVisible('webex_teams_message', 'false');


	//AJAX call for user group
	var userName = g_user.userName;
    //alert('Current user = ' + userName);

    var userGroupGA = new GlideAjax('GitUtil');
    userGroupGA.addParam('sysparm_name', 'getUserGroup');
    userGroupGA.addParam('sysparm_usernameVal', userName);
    userGroupGA.getXML(stateCallback);
    userGroupGA.getXMLAnswer(stateCallback);

    function stateCallback(answer) {
		g_form.setValue('branch_name', newBranchName + "_" + answer);	
    }
}
