sys_script_include.script:
var GitUtil = Class.create();
GitUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getUserGroup: function() {
        var userId = this.getParameter('sysparm_usernameVal');
        var UserGroupGR = new GlideRecord('sys_user_grmember');
        UserGroupGR.addQuery("user.user_name", userId);
        UserGroupGR.query();
        while (UserGroupGR.next()) {
            var userGroup = UserGroupGR.group.name.getDisplayValue();
        }

        if (userGroup == "Dev/Test") {
            return "devtest";
        } else if (userGroup === "UAT") {
            return "uat";
        } else if (userGroup == "Production") {
            return "production";
        } else userGroup = "";
        return "null";

    },

    _getUserGroupWkflow: function(userId) {
        var UserGroupGR = new GlideRecord('sys_user_grmember');
        UserGroupGR.addQuery("user.user_name", userId);
        UserGroupGR.query();
        while (UserGroupGR.next()) {
            var userGroup = UserGroupGR.group.name.getDisplayValue();
        }

        if (userGroup == "Dev/Test") {
            return "devtest";
        } else if (userGroup === "UAT") {
            return "uat";
        } else if (userGroup == "Production") {
            return "production";
        } else userGroup = "";
        return "null";

    },

    _createGitCI: function(requestNumber, ritm, sys_id, title, email, branchName, requester) {

        var ConfigItemGR = new GlideRecord('x_83068_cisco_lab_git_deployments');
        ConfigItemGR.initialize();

        ConfigItemGR.active = "false";
        ConfigItemGR.request_number = requestNumber;
        ConfigItemGR.ritm = ritm;
        ConfigItemGR.email_address = email;
        ConfigItemGR.title = title;
        ConfigItemGR.branch = branchName;
        ConfigItemGR.owned_by = requester;
        ConfigItemGR.insert();

        // get new record sys id
        ConfigItemGR.addQuery("request_number", requestNumber);
        ConfigItemGR.query();

        while (ConfigItemGR.next()) {
            var getSysId = ConfigItemGR.getValue("sys_id");
        }

        return getSysId;
    },

    _checkJobId: function(requestNumber, gitRecordSysId) {

        var gitDeploymGR = new GlideRecord('x_83068_cisco_lab_git_deployments');

        gitDeploymGR.addQuery("sys_id", gitRecordSysId);
        gitDeploymGR.addQuery("job_id", "!=", "");
        gitDeploymGR.addQuery("request_number", requestNumber);
        gitDeploymGR.addQuery("active", false);
        gitDeploymGR.query();

        var rowCount = gitDeploymGR.getRowCount();

        while (gitDeploymGR.next()) {
            if (rowCount === 1) {
                var jobId = gitDeploymGR.getValue('job_id');
            }
            return jobId;
        }
    },

    type: 'GitUtil'
});
